import { takeLatest, put, all, call } from 'redux-saga/effects';
import { auth, createUserProfileDocument, getCurrentUser, googleProvider } from '../../firebase/firebase';
import { signInFailure, signInSuccess, signOutFailure, signOutSuccess, signUpSuccess } from './user.action';
import { UserActionTypes } from './user.types';

export function* getSnapshotFromUserAuth(userAuth) {
    try {
        const userRef = yield call(createUserProfileDocument, userAuth);
        const userSnapshot = yield userRef.get();
        yield put(signInSuccess({
            id: userSnapshot.id, ...userSnapshot.data()
        }));
    } catch (error) {
        yield put(signInFailure(error));
    }
};

export function* googleSignIn() {
    try {
        const { user } = yield auth.signInWithPopup(googleProvider);
        yield getSnapshotFromUserAuth(user);
    } catch (error) {
        yield put(signInFailure(error));
    }
};

export function* emailSignIn({ payload: { email, password } }) {
    try {
        const { user } = yield auth.signInWithEmailAndPassword(email, password);
        yield getSnapshotFromUserAuth(user);
    } catch (error) {
        yield put(signInFailure(error));
    }
};

export function* signUp({ payload: { email, password, displayName } }) {
    try {
        const { user } = yield auth.createUserWithEmailAndPassword(email, password);
        const userAuth = yield createUserProfileDocument(user, displayName);
        yield put(signUpSuccess(userAuth));
    } catch (error) {
        yield put(signUpFailure(error))
    }
};

export function* signOut() {
    try {
        yield auth.signOut();
        yield put(signOutSuccess());
    } catch (error) {
        yield put(signOutFailure(error));
    }
};

export function* isUserAuthenticated() {
    try {
        const userAuth = yield getCurrentUser();
        if (!userAuth) return;
        yield getSnapshotFromUserAuth(userAuth)
    } catch (error) {
        yield put(signInFailure(error));
    }
};

export function* onSignInWithGoogle() {
    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, googleSignIn);
};

export function* onSignInWithEmail() {
    yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START, emailSignIn);
};

export function* onCheckUserSession() {
    yield takeLatest(UserActionTypes.CHECK_USER_SESSION, isUserAuthenticated);
};

export function* onSignOutStart() {
    yield takeLatest(UserActionTypes.SIGN_OUT_START, signOut)
};

export function* onSignUpStart() {
    yield takeLatest(UserActionTypes.SIGN_UP_START, signUp)
};

export function* userSagas() {
    yield all([
        call(onSignInWithGoogle),
        call(onSignInWithEmail),
        call(onCheckUserSession),
        call(isUserAuthenticated),
        call(onSignOutStart)
    ]);
};
