import React from 'react';
import { connect } from 'react-redux';
import { Route } from 'react-router';
import { convertCollectionsSnapshotToMap, firestore } from '../../firebase/firebase';
import CollectionPage from '../../pages/collection/collection';
import { updateCollections } from '../../redux/shop/shop.action';
import CollectionsOverview from '../collections-overview/collections-overview';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage)

class ShopPage extends React.Component {
    state = {
        loading: true
    };

    unsubscribeFromSnapshot = null;

    componentDidMount() {
        const { updateCollections } = this.props;
        const collectionRef = firestore.collection('collections');

        this.unsubscribeFromSnapshot = collectionRef.onSnapshot(async (snapshot) => {
            const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
            updateCollections(collectionsMap);
            this.setState({ loading: false });
        });
    }

    render() {
        const { match } = this.props
        const { loading } = this.state;
        return (
            <div className='shop-page'>
                <Route exact path={`${match.path}`}
                    render={({ loading }) => <CollectionsOverviewWithSpinner />} />
                <Route path={`${match.path}/:collectionId`}
                    render={({ loading }) => <CollectionsPageWithSpinner />} />
            </div>
        )
    }
};

const mapDispatchToProps = dispatch => ({
    updateCollections: collections =>
        dispatch(updateCollections(collections))
})

export default connect(null, mapDispatchToProps)(ShopPage);
