import { takeLatest, put, all, call } from 'redux-saga/effects';
import { auth, createUserProfileDocument, googleProvider } from '../../firebase/firebase';
import { googleSignInFailure, googleSignInSuccess } from './user.action';
import { UserActionTypes } from './user.types';

export function* googleSignIn() {
    try {
        const { user } = auth.signInWithPopup(googleProvider);
        const userRef = yield call(createUserProfileDocument, user);
        const userSnapshot = yield userRef.get();
        yield put(googleSignInSuccess({
            id: userSnapshot.id, ...userSnapshot.data()
        }))
    } catch (error) {
        yield put(googleSignInFailure(error))
    }
}

export default function* onSignInWithGoogle() {
    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, googleSignIn);
}

export function* userSagas() {
    yield all([call(onSignInWithGoogle)]);
}
