import React from 'react';

import './header.scss';
import { Link } from 'react-router-dom';
import { auth } from '../../firebase/firebase.js';
import { connect } from 'react-redux';

import { ReactComponent as Logo } from '../../assets/logo/crown.svg';
import CartIcon from '../cart-icon/cart-icon.jsx';
import CartDropdown from '../cart-dropdown/cart-dropdown.jsx';
import { selectUserCurrent } from '../../redux/users/user.selector';
import { selectCartHidden } from '../../redux/cart/cart.selector';
import { createStructuredSelector } from 'reselect';
import { HeaderContainer, LogoContainer, OptionDiv, OptionLink, OptionsContainer } from './header.styles';

const Header = ({ currentUser, hidden }) => (
    <HeaderContainer >
        <LogoContainer to="/">
            <Logo className="Logo" />
        </LogoContainer>
        <OptionsContainer >
            <OptionLink to="/shop" >
                SHOP
            </OptionLink>
            <OptionLink to="/contact" >
                CONTACT
            </OptionLink>
            {
                currentUser ?
                    <OptionDiv className="option" onClick={() => (auth.signOut())}>SIGN OUT</OptionDiv>
                    :
                    <OptionLink to="/signin" className="option">SIGN IN</OptionLink>
            }
            <CartIcon />
        </OptionsContainer>
        {hidden ? null : (<CartDropdown />)}
    </HeaderContainer>
);

const mapStateToProps = createStructuredSelector({
    currentUser: selectUserCurrent,
    hidden: selectCartHidden
});

export default connect(mapStateToProps)(Header);
