import React from 'react';
import { connect } from 'react-redux';
import { Route } from 'react-router';
import CollectionPage from '../../pages/collection/collection';
import { fetchCollectionsStartAsync } from '../../redux/shop/shop.action';
import WithSpinner from '../with-spinner/with-spinner'
import CollectionsOverview from '../collections-overview/collections-overview';
import { createStructuredSelector } from 'reselect';
import { selectIsCollectionsLoaded } from '../../redux/shop/shop.selector';
import CollectionsOverviewContainer from '../collections-overview/collection-overview.container';

const CollectionsPageWithSpinner = WithSpinner(CollectionPage)

class ShopPage extends React.Component {
    componentDidMount() {
        const { fetchCollectionsStartAsync } = this.props;
        fetchCollectionsStartAsync();
    }

    render() {
        const { match, isCollectionsLoaded } = this.props;
        return (
            <div className='shop-page'>
                <Route exact path={`${match.path}`} component={CollectionsOverviewContainer} />
                <Route path={`${match.path}/:collectionId`}
                    render={(props) => <CollectionsPageWithSpinner isLoading={!isCollectionsLoaded}{...props} />} />
            </div>
        )
    }
};

const mapStateToProps = createStructuredSelector({
    isCollectionsLoaded: selectIsCollectionsLoaded
});

const mapDispatchToProps = dispatch => ({
    fetchCollectionsStartAsync: () =>
        dispatch(fetchCollectionsStartAsync())
})

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
