import React from 'react';

import './signup.scss';
import FormInput from '../../components/form-input/form-input.jsx';
import CustomButton from '../../components/custom-button/custom-button.jsx';
import { auth, crea } from '../../firebase/firebase.js'

class SignUp extends React.Component {
    constructor() {
        super();

        this.state = {
            displayName: '',
            email: '',
            password: '',
            retypepassword: ''
        }
    }

    handleSubmit = event => {
        event.preventDefault();

        this.setState({ name: '', email: '', password: '', retypepassword: '' })
    }

    handleChange = event => {
        const { value, name } = event.target;

        this.setState({ [name]: value })
    }

    render() {
        return (
            <div className="sign-up">
                <h2 className="title">I don't have an account</h2>
                <span>Sign up with your credentials</span>

                <form onSubmit={this.handleSubmit} >
                    <FormInput name="displayName"
                        type="name"
                        label="Name"
                        value={this.state.name}
                        onSubmit={this.handleSubmit}
                        handleChange={this.handleChange}
                        required />
                    <FormInput name="email"
                        type="email"
                        label="Email"
                        value={this.state.email}
                        onSubmit={this.handleSubmit}
                        handleChange={this.handleChange}
                        required />
                    <FormInput name="password"
                        type="password"
                        label="Password"
                        value={this.state.password}
                        onSubmit={this.handleSubmit}
                        handleChange={this.handleChange}
                        required />
                    <FormInput name="retypepassword"
                        type="password"
                        label="Retype Password"
                        value={this.state.retypepassword}
                        onSubmit={this.handleSubmit}
                        handleChange={this.handleChange}
                        required />
                    <CustomButton type="Submit">Sign Up</CustomButton>
                </form>
            </div>
        )
    }
}

export default SignUp;
