import React from 'react';

import './signup.scss';
import FormInput from '../../components/form-input/form-input.jsx';
import CustomButton from '../../components/custom-button/custom-button.jsx';
import { auth, createUserProfileDocument } from '../../firebase/firebase.js'

class SignUp extends React.Component {
    constructor() {
        super();

        this.state = {
            displayName: '',
            email: '',
            password: '',
            confirmPassword: ''
        }
    }

    handleSubmit = event => {
        event.preventDefault();

        this.setState({ name: '', email: '', password: '', retypepassword: '' })
    }

    handleChange = event => {
        const { value, name } = event.target;

        this.setState({ [name]: value })
    }

    render() {
        const { displayName, email, password, confirmPassword } = this.state;
        return (
            <div className="sign-up">
                <h2 className="title">I don't have an account</h2>
                <span>Sign up with your credentials</span>

                <form onSubmit={this.handleSubmit} >
                    <FormInput name="displayName"
                        type="name"
                        label="Name"
                        value={name}
                        onSubmit={this.handleSubmit}
                        handleChange={this.handleChange}
                        required />
                    <FormInput name="email"
                        type="email"
                        label="Email"
                        value={email}
                        onSubmit={this.handleSubmit}
                        handleChange={this.handleChange}
                        required />
                    <FormInput name="password"
                        type="password"
                        label="Password"
                        value={password}
                        onSubmit={this.handleSubmit}
                        handleChange={this.handleChange}
                        required />
                    <FormInput name="confirmPassword"
                        type="password"
                        label="Confirm Password"
                        value={confirmPassword}
                        onSubmit={this.handleSubmit}
                        handleChange={this.handleChange}
                        required />
                    <CustomButton type="Submit">Sign Up</CustomButton>
                </form>
            </div>
        )
    }
}

export default SignUp;
